import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminDashboard = () => {
  const [companies, setCompanies] = useState([]);

  useEffect(() => {
    const fetchCompanies = async () => {
      try {
        // Log headers to check for any issues
        const headers = {
          'x-admin-key': process.env.REACT_APP_ADMIN_KEY, // Ensure this is set correctly in .env
        };
        console.log("Headers: ", headers);

        const response = await axios.get('/api/admin/companies', { headers });
        setCompanies(response.data);
      } catch (error) {
        console.error("Error fetching companies: ", error);
      }
    };

    fetchCompanies();
  }, []);

  return (
    <div>
      <h2>Admin Dashboard</h2>
      <ul>
        {companies.map((company) => (
          <li key={company.id}>
            {company.name} - CTC: {company.ctc}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminDashboard;
