import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminDashboard = () => {
  const [companies, setCompanies] = useState([]);

  useEffect(() => {
    const fetchCompanies = async () => {
      try {
        // Use the correct port (3000) and endpoint for fetching companies
        const response = await axios.get('http://localhost:3000/api/admin/companies');
        console.log('Companies fetched:', response.data); // Log the fetched data
        setCompanies(response.data);
      } catch (error) {
        console.error('Error fetching companies:', error); // Log the error
      }
    };
    fetchCompanies();
  }, []);

  return (
    <div>
      <h2>Admin Dashboard</h2>
      <ul>
        {companies.length > 0 ? (
          companies.map((company) => (
            <li key={company._id}>
              {company.name} - CTC: {company.ctc}
            </li>
          ))
        ) : (
          <li>No companies found</li>
        )}
      </ul>
    </div>
  );
};

export default AdminDashboard;
