import React from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate for routing

const AdminDashboard = () => {
    const navigate = useNavigate(); // Initialize the useNavigate hook

    // Handlers to navigate to student and company details
    const handleStudentDetails = () => {
        navigate('/admin/student-details');
    };

    const handleCompanyDetails = () => {
        navigate('/admin/company-details');
    };

    // Handler for logging out
    const handleLogout = () => {
        // Clear any authentication data (if needed)
        // For example: localStorage.removeItem('adminToken'); // Uncomment if you store a token
        navigate('/login'); // Redirect to the login page
    };

    return (
        <div style={{ position: 'relative', padding: '20px' }}>
            <h1>Admin Dashboard</h1>

            {/* Logout button styled for the top right corner */}
            <button 
                onClick={handleLogout} 
                style={{
                    position: 'absolute', 
                    top: '5px', // Adjust this value for vertical positioning
                    right: '10px', // Adjust this value for horizontal positioning
                    padding: '10px 15px'
                }}
            >
                Log out
            </button>

            <div style={{ marginTop: '60px' }}> {/* Adjust margin for button */}
                <button onClick={handleStudentDetails}>Go to Student Details</button>
                <button onClick={handleCompanyDetails}>Go to Company Details</button>
            </div>
        </div>
    );
};

export default AdminDashboard;
