import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const CompanyDetails = () => {
    const [companies, setCompanies] = useState([]);
    const [newCompany, setNewCompany] = useState({
        name: '',
        ctc: '',
        location: '',
        rounds: ''
    });
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const fetchCompanies = async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/admin/companies', {
                headers: {
                    'x-admin-key': 'PLMokn15',
                },
            });
            setCompanies(response.data);
        } catch (error) {
            console.error('Error fetching companies:', error);
        }
    };

    const handleAddCompany = async (e) => {
        e.preventDefault();
        try {
            await axios.post('http://localhost:5000/api/admin/add-company', newCompany, {
                headers: {
                    'x-admin-key': 'PLMokn15',
                },
            });
            fetchCompanies(); // Refresh the company list after adding
            setNewCompany({ name: '', ctc: '', location: '', rounds: '' }); // Clear form after submit
        } catch (error) {
            console.error('Error adding company:', error);
        }
    };

    useEffect(() => {
        fetchCompanies();
    }, []);

    const tableStyle = {
        borderCollapse: 'collapse',
        width: '100%',
    };

    const thTdStyle = {
        border: '1px solid black',
        padding: '8px',
        textAlign: 'left',
    };

    return (
        <div>
            <h2>Company Details Page</h2>
            {/* Form to add new company */}
            <form onSubmit={handleAddCompany}>
                <input
                    type="text"
                    placeholder="Company Name"
                    value={newCompany.name}
                    onChange={(e) => setNewCompany({ ...newCompany, name: e.target.value })}
                    required
                />
                <input
                    type="number"
                    placeholder="CTC (₹)"
                    value={newCompany.ctc}
                    onChange={(e) => setNewCompany({ ...newCompany, ctc: e.target.value })}
                    required
                />
                <input
                    type="text"
                    placeholder="Location"
                    value={newCompany.location}
                    onChange={(e) => setNewCompany({ ...newCompany, location: e.target.value })}
                    required
                />
                <input
                    type="number"
                    placeholder="Number of Rounds"
                    value={newCompany.rounds}
                    onChange={(e) => setNewCompany({ ...newCompany, rounds: e.target.value })}
                    required
                />
                <button type="submit">Add Company</button>
            </form>

            {/* Displaying the list of companies */}
            {companies.length > 0 ? (
                <table style={tableStyle}>
                    <thead>
                        <tr>
                            <th style={thTdStyle}>Company Name</th>
                            <th style={thTdStyle}>Role Offered</th>
                            <th style={thTdStyle}>CTC (₹)</th>
                            <th style={thTdStyle}>Location</th>
                            <th style={thTdStyle}>Number of Rounds</th>
                            <th style={thTdStyle}>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {companies.map((company) => (
                            <tr key={company._id}>
                                <td style={thTdStyle}>{company.name}</td>
                                <td style={thTdStyle}>{company.role || 'N/A'}</td>
                                <td style={thTdStyle}>{company.ctc}</td>
                                <td style={thTdStyle}>{company.location || 'N/A'}</td>
                                <td style={thTdStyle}>{company.rounds || 'N/A'}</td>
                                <td style={thTdStyle}>
                                    {/* Edit and Delete buttons */}
                                    <button onClick={() => navigate(`/admin/edit-company/${company._id}`)}>Edit</button>
                                    <button onClick={() => handleDeleteCompany(company._id)}>Delete</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            ) : (
                <p>No companies found</p>
            )}
            <button onClick={() => navigate('/admin-dashboard')} style={{ marginTop: '20px', padding: '10px 20px' }}>
                Back to Admin Dashboard
            </button>
        </div>
    );
};

export default CompanyDetails;
