import React, { useState, useEffect } from 'react';

const CompanyDetails = () => {
  const [companies, setCompanies] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    ctc: '',
    eligibility: '',
    rounds: '',
    role: '',
    location: ''
  });

  useEffect(() => {
    fetchCompanies();
  }, []);

  const fetchCompanies = async () => {
    try {
      const response = await fetch('/admin/companies');
      const data = await response.json();
      setCompanies(data);
    } catch (error) {
      console.error('Error fetching companies:', error);
    }
  };

  // Handle form input changes
  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  // Handle add company form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('/admin/add-company', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });
      const result = await response.json();
      if (response.ok) {
        console.log('Company added successfully', result);
        fetchCompanies(); // Refresh company list
      } else {
        console.log('Error adding company:', result.message);
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div>
      <h2>Company Details</h2>
      <ul>
        {companies.map((company) => (
          <li key={company._id}>
            {company.name} - {company.ctc} - {company.eligibility} - {company.rounds} - {company.role} - {company.location}
          </li>
        ))}
      </ul>

      <h3>Add a New Company</h3>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          placeholder="Company Name"
          value={formData.name}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="ctc"
          placeholder="CTC"
          value={formData.ctc}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="eligibility"
          placeholder="Eligibility"
          value={formData.eligibility}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="rounds"
          placeholder="Rounds"
          value={formData.rounds}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="role"
          placeholder="Role"
          value={formData.role}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="location"
          placeholder="Location"
          value={formData.location}
          onChange={handleChange}
          required
        />
        <button type="submit">Add Company</button>
      </form>
    </div>
  );
};

export default CompanyDetails;
