import React, { useEffect, useState } from 'react';
import axios from 'axios';

const CompanyDetails = () => {
    const [companies, setCompanies] = useState([]);

    const fetchCompanies = async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/admin/companies', {
                headers: {
                    'x-admin-key': 'PLMokn15', // Use your admin key here
                },
            });
            setCompanies(response.data);
        } catch (error) {
            console.error('Error fetching companies:', error);
        }
    };

    useEffect(() => {
        fetchCompanies();
    }, []);

    const tableStyle = {
        borderCollapse: 'collapse',
        width: '100%',
    };

    const thTdStyle = {
        border: '1px solid black',
        padding: '8px',
        textAlign: 'left',
    };

    return (
        <div>
            <h2>Company Details Page</h2>
            {companies.length > 0 ? (
                <table style={tableStyle}>
                    <thead>
                        <tr>
                            <th style={thTdStyle}>Company Name</th>
                            <th style={thTdStyle}>Role Offered</th>
                            <th style={thTdStyle}>CTC (â‚¹)</th>
                            <th style={thTdStyle}>Location</th>
                            <th style={thTdStyle}>Number of Rounds</th>
                        </tr>
                    </thead>
                    <tbody>
                        {companies.map((company) => (
                            <tr key={company._id}>
                                <td style={thTdStyle}>{company.name}</td>
                                <td style={thTdStyle}>{company.role || 'N/A'}</td> {/* Default to 'N/A' if no role */}
                                <td style={thTdStyle}>{company.ctc}</td>
                                <td style={thTdStyle}>{company.location || 'N/A'}</td> {/* Default to 'N/A' if no location */}
                                <td style={thTdStyle}>{company.rounds || 'N/A'}</td> {/* Default to 'N/A' if no rounds */}
                            </tr>
                        ))}
                    </tbody>
                </table>
            ) : (
                <p>No companies found</p>
            )}
        </div>
    );
};

export default CompanyDetails;
