import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import vpsLogo from './images/vps.png';
import './CompanyDetails.css'; 

const CompanyDetails = () => {
    const [dropdownOpen, setDropdownOpen] = useState(false);
    const [companies, setCompanies] = useState([]);
    const [newCompany, setNewCompany] = useState({ name: '', ctc: '', role: '', location: '', rounds: '' });
    const [editCompany, setEditCompany] = useState(null); // To store company being edited
    const navigate = useNavigate();

    const fetchCompanies = async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/admin/companies', {
                headers: {
                    'x-admin-key': 'PLMokn15', // Use your admin key here
                },
            });
            setCompanies(response.data);
        } catch (error) {
            console.error('Error fetching companies:', error);
        }
    };

    const toggleDropdown = () => {
        setDropdownOpen(!dropdownOpen);
    };

    const handleLogout = () => {
        navigate('/'); // Navigate to the login page
    };

    useEffect(() => {
        fetchCompanies();
    }, []);

    const handleDeleteCompany = async (companyId) => {
        try {
            await axios.delete(`http://localhost:5000/api/admin/delete-company/${companyId}`, {
                headers: {
                    'x-admin-key': 'PLMokn15', // Admin key for authentication
                },
            });
            fetchCompanies(); // Refresh the company list after deletion
        } catch (error) {
            console.error('Error deleting company:', error);
        }
    };

    const handleAddCompany = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:5000/api/admin/add-company', newCompany, {
                headers: {
                    'x-admin-key': 'PLMokn15', // Admin key for authentication
                },
            });
            setCompanies([...companies, response.data]); // Add the new company to the list
            setNewCompany({ name: '', ctc: '', role: '', location: '', rounds: '' }); // Reset the form
        } catch (error) {
            console.error('Error adding company:', error.response ? error.response.data : error.message);
        }
    };

    const handleEditCompany = async (e) => {
        e.preventDefault();
        try {
            await axios.put(`http://localhost:5000/api/admin/edit-company/${editCompany._id}`, editCompany, {
                headers: {
                    'x-admin-key': 'PLMokn15', // Admin key for authentication
                },
            });
            fetchCompanies(); // Refresh the company list after editing
            setEditCompany(null); // Close edit form
        } catch (error) {
            console.error('Error editing company:', error);
        }
    };

    return (
        <div className="company-details-container">
            <img 
                src={vpsLogo} 
                alt="Logo"
                className="logo"
                onClick={toggleDropdown}
            />
            {dropdownOpen && (
                <div className="dropdown">
                    <button onClick={() => navigate('/admin-dashboard')}>Home</button>
                    <button onClick={handleLogout}>Logout</button>
                </div>
            )}
            <h2>Company Details Page</h2>

            {/* Main Content Container */}
            <div className="content-container">
                {/* Form Section */}
                <div className="form-section">
                    <h3>{editCompany ? 'Edit Company' : 'Add New Company'}</h3>
                    <form onSubmit={editCompany ? handleEditCompany : handleAddCompany}>
                        <input
                            type="text"
                            placeholder="Company Name"
                            value={editCompany ? editCompany.name : newCompany.name}
                            onChange={(e) => editCompany ? setEditCompany({ ...editCompany, name: e.target.value }) : setNewCompany({ ...newCompany, name: e.target.value })}
                            required
                        />
                        <input
                            type="number"
                            placeholder="CTC (₹)"
                            value={editCompany ? editCompany.ctc : newCompany.ctc}
                            onChange={(e) => editCompany ? setEditCompany({ ...editCompany, ctc: e.target.value }) : setNewCompany({ ...newCompany, ctc: e.target.value })}
                            required
                        />
                        <input
                            type="text"
                            placeholder="Role Offered"
                            value={editCompany ? editCompany.role : newCompany.role}
                            onChange={(e) => editCompany ? setEditCompany({ ...editCompany, role: e.target.value }) : setNewCompany({ ...newCompany, role: e.target.value })}
                        />
                        <input
                            type="text"
                            placeholder="Location"
                            value={editCompany ? editCompany.location : newCompany.location}
                            onChange={(e) => editCompany ? setEditCompany({ ...editCompany, location: e.target.value }) : setNewCompany({ ...newCompany, location: e.target.value })}
                        />
                        <input
                            type="number"
                            placeholder="Number of Rounds"
                            value={editCompany ? editCompany.rounds : newCompany.rounds}
                            onChange={(e) => editCompany ? setEditCompany({ ...editCompany, rounds: e.target.value }) : setNewCompany({ ...newCompany, rounds: e.target.value })}
                        />
                        <button type="submit">{editCompany ? 'Update Company' : 'Add Company'}</button>
                    </form>
                </div>

                {/* Company Details Section */}
                <div className="company-table-section">
                    <h3>Company List</h3>
                    {companies.length > 0 ? (
                        <table>
                            <thead>
                                <tr>
                                    <th>Company Name</th>
                                    <th>Role Offered</th>
                                    <th>CTC (₹)</th>
                                    <th>Location</th>
                                    <th>Number of Rounds</th>
                                    <th>Actions</th>
                                </tr>
                            </thead>
                            <tbody>
                                {companies.map((company) => (
                                    <tr key={company._id}>
                                        <td>{company.name}</td>
                                        <td>{company.role || 'N/A'}</td>
                                        <td>{company.ctc}</td>
                                        <td>{company.location || 'N/A'}</td>
                                        <td>{company.rounds || 'N/A'}</td>
                                        <td>
                                            <button onClick={() => setEditCompany(company)}>Edit</button>
                                            <button onClick={() => handleDeleteCompany(company._id)} style={{ marginLeft: '10px' }}>Delete</button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    ) : (
                        <p>No companies found</p>
                    )}
                </div>
            </div>

            {/* Back Button */}
            <button onClick={() => navigate('/admin-dashboard')}>Back to Admin Dashboard</button>
        </div>
    );
};

export default CompanyDetails;
